function tow(t){return t>=0&&t<10?"0"+t:""+t}let oSpan=document.getElementsByTagName("timing")[0],localhostTime=new Date;setInterval(function(){let t=new Date,a=t.getTime()-localhostTime.getTime();var e=Math.floor(a/1e3);Math.floor(e/86400);e%=86400;var n=Math.floor(e/3600);e%=3600;var o=Math.floor(e/60);e%=60;var s=tow(n)+'<span class="time">小时</span>'+tow(o)+'<span class="time">分钟</span>'+tow(e)+'<span class="time">秒</span>';oSpan.innerHTML="您已浏览网页"+s},1e3);